package {PKG_NAME};

// ...existing code...
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;
import java.nio.file.StandardOpenOption;

public class OutputUtil {
    // Replace {STUDENT_ID} with your actual student ID (or set at build time)
    public static final String STUDENT_ID = "{STUDENT_ID}";

    public static void printWithId(String output, String filename) {
        String finalOutput = output + STUDENT_ID;
        System.out.println(finalOutput);
        if (filename != null && !filename.isEmpty()) {
            try {
                Files.write(Paths.get(filename), (finalOutput + System.lineSeparator()).getBytes(),
                        StandardOpenOption.CREATE, StandardOpenOption.APPEND);
            } catch (IOException e) {
                System.err.println("Failed to write output file: " + e.getMessage());
            }
        }
    }

    public static void printWithId(String output) {
        printWithId(output, null);
    }
}

